t.script:

document.addEventListener('DOMContentLoaded', function() {
    console.log("Enhanced Attendance JS loaded");

    // Live timer for current session with timezone awareness
    function updateLiveTimer() {
        const timerElement = document.getElementById('live-timer');
        if (timerElement) {
            // We'll use a data attribute to store the check-in time
            // This will be in ISO format and we'll convert to the browser's local time
            const checkInTimeISO = '{{ today_att.check_in if today_att else "" }}';
            if (checkInTimeISO) {
                const checkInTime = new Date(checkInTimeISO);
                if (!isNaN(checkInTime.getTime())) {
                    const now = new Date();
                    const diff = now - checkInTime;
                    const hours = Math.floor(diff / (1000 * 60 * 60));
                    const minutes = Math.floor((diff % (1000 * 60 * 60)) / (1000 * 60));
                    timerElement.innerHTML = `<strong>${hours}h ${minutes}m</strong>`;
                    timerElement.title = `Using timezone: ${Intl.DateTimeFormat().resolvedOptions().timeZone}`;
                }
            }
        }
    }

    // Update timer every minute
    setInterval(updateLiveTimer, 60000);
    updateLiveTimer(); // Initial call

    // Helper: Reverse geocode using Nominatim (OpenStreetMap) with error handling and retries
    function reverseGeocode(lat, lon, callback) {
        console.log(`Attempting to reverse geocode: ${lat}, ${lon}`);

        // Add cache to prevent excessive API calls
        const cacheKey = `${lat.toFixed(6)},${lon.toFixed(6)}`;
        const cachedResult = sessionStorage.getItem(`geo_${cacheKey}`);
        if (cachedResult) {
            console.log(`Using cached location: ${cachedResult}`);
            callback(cachedResult);
            return;
        }

        // Add a user agent header to comply with Nominatim usage policy
        fetch(`https://nominatim.openstreetmap.org/reverse?format=json&lat=${lat}&lon=${lon}&zoom=18`, {
            headers: {
                'User-Agent': 'TechcarrotESS/1.0'
            }
        })
        .then(response => {
            if (!response.ok) {
                throw new Error(`HTTP error! Status: ${response.status}`);
            }
            return response.json();
        })
        .then(data => {
            if (data && data.display_name) {
                console.log(`Got location: ${data.display_name}`);
                // Cache the result for future use
                sessionStorage.setItem(`geo_${cacheKey}`, data.display_name);
                callback(data.display_name);
            } else {
                console.warn('No location name in response', data);
                callback(`Location at ${lat.toFixed(6)},${lon.toFixed(6)}`);
            }
        })
        .catch(error => {
            console.error('Error in reverse geocoding:', error);
            // Fallback to coordinates
            callback(`Location at ${lat.toFixed(6)},${lon.toFixed(6)}`);
        });
    }

    // Enhanced Check-In with better UX
    const checkinForm = document.getElementById('checkin-form');
    if (checkinForm) {
        const checkinBtn = checkinForm.querySelector('button[type="submit"]');

        checkinForm.addEventListener('submit', function(e) {
            e.preventDefault();

            // Show loading state
            const originalText = checkinBtn.innerHTML;
            checkinBtn.innerHTML = '<i class="fa fa-spinner fa-spin"></i> Checking In...';
            checkinBtn.disabled = true;

            if (navigator.geolocation) {
                navigator.geolocation.getCurrentPosition(function(position) {
                    document.getElementById('in_latitude').value = position.coords.latitude;
                    document.getElementById('in_longitude').value = position.coords.longitude;
                    document.getElementById('geo-error').classList.add('d-none');

                    reverseGeocode(position.coords.latitude, position.coords.longitude, function(address) {
                        document.getElementById('check_in_location').value = address;
                        checkinForm.submit();
                    });
                }, function(error) {
                    console.error('Geolocation error:', error);
                    document.getElementById('geo-error').classList.remove('d-none');

                    // Reset button state
                    checkinBtn.innerHTML = originalText;
                    checkinBtn.disabled = false;

                    // Submit without location
                    if (confirm('Location access failed. Continue without location data?')) {
                        checkinForm.submit();
                    }
                }, {
                    enableHighAccuracy: true,
                    timeout: 10000,
                    maximumAge: 60000
                });
            } else {
                document.getElementById('geo-error').classList.remove('d-none');
                checkinBtn.innerHTML = originalText;
                checkinBtn.disabled = false;
            }
        });
    }

    // Enhanced Check-Out with better UX
    const checkoutForm = document.getElementById('checkout-form');
    if (checkoutForm) {
        const checkoutBtn = checkoutForm.querySelector('button[type="submit"]');

        checkoutForm.addEventListener('submit', function(e) {
            e.preventDefault();

            // Show loading state
            const originalText = checkoutBtn.innerHTML;
            checkoutBtn.innerHTML = '<i class="fa fa-spinner fa-spin"></i> Checking Out...';
            checkoutBtn.disabled = true;

            if (navigator.geolocation) {
                navigator.geolocation.getCurrentPosition(function(position) {
                    document.getElementById('out_latitude').value = position.coords.latitude;
                    document.getElementById('out_longitude').value = position.coords.longitude;
                    document.getElementById('geo-error').classList.add('d-none');

                    reverseGeocode(position.coords.latitude, position.coords.longitude, function(address) {
                        document.getElementById('check_out_location').value = address;
                        checkoutForm.submit();
                    });
                }, function(error) {
                    console.error('Geolocation error:', error);
                    document.getElementById('geo-error').classList.remove('d-none');

                    // Reset button state
                    checkoutBtn.innerHTML = originalText;
                    checkoutBtn.disabled = false;

                    // Submit without location
                    if (confirm('Location access failed. Continue without location data?')) {
                        checkoutForm.submit();
                    }
                }, {
                    enableHighAccuracy: true,
                    timeout: 10000,
                    maximumAge: 60000
                });
            } else {
                document.getElementById('geo-error').classList.remove('d-none');
                checkoutBtn.innerHTML = originalText;
                checkoutBtn.disabled = false;
            }
        });
    }

    // Auto-dismiss alerts after 5 seconds
    setTimeout(function() {
        const alerts = document.querySelectorAll('.alert-dismissible');
        alerts.forEach(function(alert) {
            if (alert.classList.contains('show')) {
                alert.classList.remove('show');
                alert.classList.add('fade');
            }
        });
    }, 5000);

    // Table enhancements
    const table = document.querySelector('.table-responsive table');
    if (table) {
        // Add hover effects and click handlers
        const rows = table.querySelectorAll('tbody tr');
        rows.forEach(function(row) {
            row.addEventListener('click', function() {
                // Highlight selected row
                rows.forEach(r => r.classList.remove('table-primary'));
                row.classList.add('table-primary');
            });
        });
    }
});
